package Class02NonPrimitive;

public class VariableCreation {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//1st way to create a variable
			byte b=10; //create a variable and assign value
		
		//2nd way to create a variable
			int i; //create a variable 
			i=100; //assign value
		
			//we need to create a variable only once
			//then we can use as many times as we want/need
			
		//3rd way to create a variable
			//create a variable = declare a variable - different terminology
			int num1, num2, num3; 
			
			num1=100; //assign value = initialize variable -different term
			num2=200;
			num3=300;
			//variables must be on different lines
			System.out.println(num2);
			
			//int num1=100; error -> duplicate variable
			
			//? Can I change a value of a variable? YES
			num2=2000; //reassign the value
			
			System.out.println(num2); 
			
			
	}

}
